import React from "react";

// import material-ui components
import { makeStyles } from "@material-ui/core/styles";
import { Box, Grid } from "@material-ui/core";

// import image
import aboutPic from "../assets/aboutPic.jpg";

const useStyles = makeStyles({
  fullHeight: {
    height: "100vh"
  },
  aboutDiv: {
    width: "70vw",
    fontSize: "20px"
  },
  picStyling: {
    width: "250px",
    height: "250px",
    borderRadius: "50%",
    objectFit: "cover"
  },
  center: {
    display: "flex",
    justifyContent: "center"
  },
  skillsBox: {
    backgroundColor: "pink",
    height: "6vh",
    color: "white",
    marginTop: "2rem"
  },
  aboutBanner: {
    textAlign: "center",
    fontSize: "55px",
    marginBottom: "-1rem"
  }
});

function About() {
  const classes = useStyles();
  return (
    <div className={classes.fullHeight}>
      <Box
        justifyContent="center"
        alignItems="center"
        display="flex"
        id="about"
      >
        <div className={classes.aboutDiv}>
          <Grid
            container
            spacing={5}
            direction="row"
            alignItems="center"
            justify="center"
          >
            <Grid item xs={12}>
              <h1 className={classes.aboutBanner}>About Me</h1>
            </Grid>
            <Grid item xs={12} md={3} className={classes.center}>
              <img
                src={aboutPic}
                alt="self portrait"
                className={classes.picStyling}
              ></img>
            </Grid>
            <Grid item xs={12} md={9}>
              <p>
                Hi, I'm Jay! I am a full stack developer from sunny Orange
                County, California. I build clean, user-focused interfaces and
                help clients maximize their web presence and highlight the
                importance of their organization. I would love to start a
                conversation with you and work with you on your next project!
                Check out some of my previous work below.
              </p>
            </Grid>
          </Grid>

          <Box
            className={classes.skillsBox}
            display="flex"
            justifyContent="center"
            alignItems="center"
          >
            <h1>Skills</h1>
          </Box>
          <Grid container>
            <Grid item xs={3}>
              <p>HTML5</p>
              asdf
            </Grid>
            <Grid item xs={3}>
              <p>CSS3</p>
            </Grid>
            <Grid item xs={3}>
              <p>JavaScript</p>
            </Grid>
            <Grid item xs={3}>
              <p>ReactJS</p>
            </Grid>
          </Grid>
        </div>
      </Box>
    </div>
  );
}

export default About;
