import React from "react";

// import background image
import Projectsbg from "../assets/projectsbg.jpg";

// import material-ui components
import { makeStyles } from "@material-ui/core/styles";
import { Box, Grid } from "@material-ui/core";

// import internal components
import Projectinfo from "./projectinfo";

// import json file with information for each project to be mapped
import projectinfo from "../utils/projectinfo.json";

// custom css styling
const useStyles = makeStyles({
  projectsBackground: {
    backgroundImage: `url(${Projectsbg})`
  },
  projectImg: {
    width: "20vw"
  }
});

function Projects() {
  const classes = useStyles();

  return (
    <div className={classes.projectsBackground}>
      <Box display="flex" justifyContent="center">
        <h1 style={{ fontSize: "50px" }}>Previous Works</h1>
      </Box>
      {/* {projectinfo.map((item, i) => (
        <Projectinfo key={i} data={item} />
      ))} */}
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={5}
      >
        <Grid item xs={12} md={4}>
          <h1>{data.name}</h1>
          <p>{data.desc}</p>
        </Grid>
        <Grid item>
          <img
            src={require(`../assets/${data.img}.jpg`).default}
            alt="screenshot of my project"
            className={classes.projectImg}
          ></img>
        </Grid>
      </Grid>
    </div>
  );
}

export default Projects;
