import React from "react";

// import material-ui components
import { makeStyles } from "@material-ui/core/styles";
import { Grid, Modal, Button } from "@material-ui/core";

// custom css styling
const useStyles = makeStyles(theme => ({
  linkText: {
    textDecoration: "none",
    color: "black"
  },
  projectImg: {
    width: "40vw",
    height: "15vh",
    [theme.breakpoints.only("md")]: {
      width: "40vw",
      height: "12vh"
    },
    [theme.breakpoints.only("sm")]: {
      width: "40vw",
      height: "10vh"
    },
    [theme.breakpoints.only("xs")]: {
      width: "80%",
      height: "8%"
    }
  },
  modalStyling: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    overflow: "scroll"
  },
  paper: {
    position: "absolute",
    width: 400,
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(2, 4, 3)
  }
}));

function Projectinfo({ data }) {
  const classes = useStyles();

  //hooks
  const [openProject, setOpenProject] = React.useState(false);
  const handleProjectOpen = () => {
    setOpenProject(true);
  };
  const handleProjectClose = () => {
    setOpenProject(false);
  };

  const modalBody = (
    <div className={classes.paper}>
      <Grid item xs={12} md={5}>
        <a
          href={data.link}
          target="_blank"
          rel="noopener noreferrer"
          className={classes.linkText}
        >
          <h3>Description</h3>
          <p>{data.desc}</p>
          <h3>Technologies Used</h3>
          <p>{data.tech}</p>
        </a>
      </Grid>
    </div>
  );

  return (
    <div style={{ margin: "auto", width: "75%" }}>
      <div data-aos="flip-up" data-aos-delay="50" data-aos-duration="1000">
        <Grid
          container
          direction="column"
          alignItems="center"
          justifyContent="center"
        >
          <h2>{data.name}</h2>
          <Grid item xs={12}>
            <Button type="button" onClick={handleProjectOpen}>
              <img
                src={require(`../assets/${data.img}.jpg`).default}
                alt="screenshot of my project"
                className={`${classes.projectImg} ${classes.linkAnimate}`}
              ></img>
            </Button>
          </Grid>

          <Modal
            open={openProject}
            onClose={handleProjectClose}
            aria-labelledby="project-modal"
            aria-describedby="project description"
            className={classes.modalStyling}
          >
            {modalBody}
          </Modal>
        </Grid>
      </div>
    </div>
  );
}

export default Projectinfo;
