import React from "react";

// import material-ui components
import { makeStyles } from "@material-ui/core/styles";
import { Box, Grid } from "@material-ui/core";

// import image
import aboutPic from "../img/aboutPic.jpg";

const useStyles = makeStyles({
  aboutDiv: {
    width: "70vw",
    fontSize: "20px"
  },
  picStyling: {
    width: "100px",
    borderRadius: "50%",
    overflow: "hidden"
  }
});

function About() {
  const classes = useStyles();
  return (
    <>
      <Box
        justifyContent="center"
        alignItems="center"
        display="flex"
        id="about"
        style={{ height: "50vh" }}
      >
        <div className={classes.aboutDiv}>
          <h1>About</h1>
          <img
            src={aboutPic}
            alt="self portrait"
            className={classes.picStyling}
          ></img>
          <p>
            Hi, I'm Jay. I am a full stack developer in sunny Orange County, CA.
            I enjoy seeing dreams slowly come to life and would love to work
            with you on your next project! I come from a background of cognitive
            psychology and I am known for delivering user-focused designs
            through researching, iterating, and testing. Looking forward to help
            your ideas reach their maximum potential.
          </p>
          <hr style={{ marginTop: "2rem", marginBottom: "2rem" }}></hr>
          <Grid>
            <Grid item>hello</Grid>
          </Grid>
        </div>
      </Box>
    </>
  );
}

export default About;
